sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   if(!g_form.isNewRecord()) {
  //***Change the column names [1st parameter] as per your needs****//
	if(!g_user.hasRole('x_1087004_jail_vis.staff')){
       g_form.setReadOnly('area', true);
       g_form.setReadOnly('number_of_visitors', true);
       g_form.setReadOnly('number', true);
       g_form.setReadOnly('inmate', true);
       g_form.setReadOnly('applicant', true);
       g_form.setReadOnly('reason_inquiry', true);
       g_form.setReadOnly('assigned_t0', true);
       g_form.setReadOnly('state', true);
       g_form.setReadOnly('date_time', true);
       g_form.clearOptions('approva0');
       g_form.addOption("approva0","pending","Pending");
       g_form.addOption("approva0","cancelled","Cancel");
      }
	}
}
